import React, { useState } from 'react'

export default function CountChar(){
const [numberSequence, setNumberSequence] = useState(0)

    function countChar(){
        var numSeq = numberSequence
        var seqArr = Array.from(String(numSeq), Number)
        var countArr = [10]
        for(let i=0; i<10; i++){
            countArr[i] = 0
        }

        for(let i=0; i<seqArr.length; i++){
            var curIndex = seqArr[i]
            countArr[curIndex] += 1
        }

        console.log(countArr)

        //output result array
        var outputdiv = document.getElementById('outputdiv')
        outputdiv.innerHTML = numSeq
    }

    return(
        <div>
            <input type={"number"} placeholder={"Enter your number sequence"} onChange={(e) => {setNumberSequence(e.target.value)}}/>
            <button onClick={countChar()}>Count</button>

            <p className="output" id="outputdiv"></p>
        </div>
    )
}
